// variation of nge on the right

class Solution {
public:
    vector<int> dailyTemperatures(vector<int>& arr){
        int n = arr.size();
        vector<int>nums(n);
        stack<int>st;
        st.push(n-1);
        nums[n-1] = 0;

        for(int i = n-2;i>=0;i--){
            while(!st.empty() && arr[st.top()]<=arr[i]){
                st.pop();
            }
            if(st.empty())nums[i] = 0;
            else nums[i] = st.top()-i;
            st.push(i);
        }
        return nums;
    }
};
