class Solution {
public:
string decodeString(string s)
{
    stack<char> st;
    for (int i = 0; i < s.length(); i++)
    {
        char c = s[i];
        if (c == ']')
        {
            string temp2 = "";
            while (!st.empty())
            {
                char temp = st.top();
                st.pop();
                if (temp == '[')
                {
                    // insert val;
                    string tempNum = "";
                    while (!st.empty())
                    {
                        int num = st.top() - '0';
                        if (num >= 0 && num <= 9)
                        {
                            tempNum.push_back(st.top());
                            st.pop();
                        }
                        else
                        {
                            break;
                        }
                    }
                    reverse(tempNum.begin(), tempNum.end());
                    int tempNumber = stoi(tempNum);
                    reverse(temp2.begin(), temp2.end());
                    for (int i = 0; i < tempNumber; i++)
                    {
                        for (int j = 0; j < temp2.length(); j++)
                        {
                            st.push(temp2[j]);
                        }
                    }
                    break;
                }
                else
                {
                    temp2.push_back(temp);
                }
            }
        }
        else
        {
            st.push(c);
        }
    }
    string ans = "";
    while (!st.empty())
    {
        ans.push_back(st.top());
        st.pop();
    }
    reverse(ans.begin(), ans.end());
    return ans;
}
};
