int dp[101];
class Solution {
public:
    int helper(string &s, int start)
    {
        if (start == s.length())
            return 1;
        if (dp[start] != -1)
            return dp[start];
        int ans = 0;
        for (int i = start; i < s.length(); i++)
        {
            string temp = s.substr(start, i - start + 1);
            int num = stoi(temp);
            if (num < 1 || num > 26)
                break;
            ans += helper(s, i + 1);
        }
        return dp[start] = ans;
    }
    int numDecodings(string s)
    {
        memset(dp, -1, sizeof dp);
        return helper(s, 0);
    }
};
