class Solution {
public:
    vector<int> decompressRLElist(vector<int>& nums) {
        int n = nums.size();
        vector<int> ans;
        for(int i = 0; i < n;i+=2){
            int a = nums[i];
            int b = nums[i + 1];
            for(int j = 0 ;j<a;j++){
                ans.push_back(b);
            }
        }
        return ans;

    }
};
