class Solution {
public:
    string freqAlphabets(string s) {
        string temp = "";
        for(int i = 0; i < s.length();i++){
            if(s[i] == '#'){
                temp.pop_back();
                temp.pop_back();
                int num = (s[i-2] - '0') * 10 + s[i-1] - '0';
                temp.push_back(num - 10 + 'j');
            }else{
                temp.push_back(s[i]);
            }
        }
        for(int i = 0; i < temp.size();i++){
            if(temp[i] >= '1' && temp[i] <='9'){
                temp[i] = temp[i] - '1' + 'a';
            }
        }
        return temp;
    }
};
