int dp[10005];
class Solution {
public:
    int helper(vector<int> &nums, int ind){
        if(ind >= nums.size()){
            return 0;
        }
        if(dp[ind] != -1)return dp[ind];        
        int take = 0, notTake = 0;
        take = nums[ind] + helper(nums, ind + 2);
        notTake = helper(nums, ind + 1);
        return dp[ind] = max(take, notTake);
    }
    int deleteAndEarn(vector<int>& nums) {
        vector<int> arr(10005, 0);
        for(auto i : nums){
            arr[i] += i;
        }
        memset(dp, -1, sizeof dp);
        return helper(arr, 0);
    }
};
