class Solution {
public:
    int minDeletionSize(vector<string>& strs) {

        int ans = 0;
        int n = strs.size();
        int m = strs[0].size();
        for(int i = 0; i < m; i++) {
            char ch = 'a';
            for(int j = 0; j < n; j++) {
                if(ch > strs[j][i]) {
                    ans++;
                    break;
                }
                else {
                    ch = strs[j][i];
                }
            }
        }
        return ans;
    }
};
