class Solution {
public:
    int deleteGreatestValue(vector<vector<int>>& grid) {
        for(auto &i : grid){
            sort(i.begin(), i.end());
        }
        int n = grid.size(), m = grid[0].size();
        int ans = 0;
        for(int j = 0; j  < m;j++){
            int _max = -1e9;
            for(int i = 0; i < n;i++){
                _max = max(_max, grid[i][j]);
            }
            ans += _max;
        }
        return ans;
    }
};
