class Solution {
public:
    int dp[505][505];
    int find(string &s, string &t, int i, int j) {
        if(i == s.size() and j == t.size()) {
            return 0;
        }
        if(i == s.size()) {
            return (t.size() - j);
        }
        if(j == t.size()) {
            return (s.size() - i);
        }
        if(dp[i][j] != -1) {
            return dp[i][j];
        }
        if(s[i] != t[j]) {
            return dp[i][j] = 1 + min(find(s, t, i + 1, j), find(s, t, i, j + 1));
        }
        return dp[i][j] = find(s, t, i + 1, j + 1);
    }
    int minDistance(string s, string t) {
        memset(dp, -1,sizeof dp);
       return find(s, t, 0, 0);
    }
};
