class DLLNode{
public:
    DLLNode *prev, *next;
    string val;
    DLLNode(string _val){
        val = _val;
        prev = next = NULL;
    }
};
class BrowserHistory {
public:
    DLLNode *head;
    BrowserHistory(string homepage) {
        head = new DLLNode(homepage); 
    }

    void visit(string url) {
        DLLNode *curr = new DLLNode(url);
        head->next = curr;
        curr->prev = head;
        head = head->next;
    }

    string back(int steps) {
        while(steps-- && head->prev != NULL)head = head->prev;
        return head->val;
    }

    string forward(int steps) {
        while(steps-- && head->next != NULL)head = head->next;
        return head->val;
    }
};

