class MyHashSet {
public:
    map<int, int> m;
    MyHashSet() {
        m.clear();
    }

    void add(int key) {
        m[key]++;
    }

    void remove(int key) {
        m.erase(key);
    }

    bool contains(int key) {
        return m.find(key) != m.end();
    }
};

