class ParkingSystem {
public:
    int cntb =0 , cntm = 0, cnts = 0;
    ParkingSystem(int big, int medium, int small) {
        cntb = big;
        cntm = medium;
        cnts = small;
    }

    bool addCar(int carType) {
        if(carType == 1){
            if(cntb > 0){
                cntb--;
                return true;
            }
            else{
                return false;
            }
        }else if(carType == 2){
            if(cntm > 0){
                cntm--;
                return true;
            }
            else{
                return false;
            }
        }else{
            if(cnts > 0){
                cnts--;
                return true;
            }
            else{
                return false;
            }
        }
    }
};

