class Solution {
public:
    int destroyTargets(vector<int>& nums, int space) {
        map<int, int> mp;
        sort(nums.begin(), nums.end());
        for(auto i : nums){
            mp[i%space]++;
        }
        int _max = 0;
        for(auto i : mp){
            _max = max(_max, i.second);
        }
        for(auto i : nums){
            if(mp[i%space] == _max)return i;
        }
        return -1;
    }
};
