class Solution {
public:
    int minimumSum(int n, int k) {
        unordered_map<int,int> umap;
        int sum=0;
        int i=1;
        while(n){
            if(!umap.count(k-i)){
                cout<<i<<" ";
                umap[i]++;
                sum+=i;
                n--;
            }
            i++;
        }
        cout<<endl;
        return sum;
    }
};
