class Solution {
public:
    vector<int> diStringMatch(string s) {
        int n = s.length();
        int inc = 0;
        int dec = n;
        vector<int> ans(n+1,0);
        for(int i=0;i<n;i++){
            if(s[i] == 'I') {
                ans[i] = inc++;

            }
                else{
                    ans[i] = dec--;
                }
        }

        ans[n] = inc;

        return ans;
    }
};
