class Solution {
public:

    vector<int> diffWaysToCompute(string e) {
        vector<int> res;
        for(int i = 0; i < e.size(); i++) {
            char ch = e[i];
            if(ch == '+' || ch == '-' || ch == '*') {
                vector<int> left = diffWaysToCompute(e.substr(0, i));


                vector<int> right = diffWaysToCompute(e.substr(i + 1));

                for(auto j : left) {
                    for(auto k : right) {

                        if(ch == '+') {
                            res.push_back(j + k);
                        }
                        else if(ch == '-') {
                            res.push_back(j - k);

                        }
                        else {
                            res.push_back(j * k);
                        }
                    }
                }
            }
        }
        if(res.empty()) {
            res.push_back(stoi(e));
        }
        return res;
    }
};
