class Solution {
public:
    vector<int> distributeCandies(int candies, int num_people) {
        vector<int> ans(num_people, 0);
        int temp = 0;
        int ind = 0, t = 1;
        while(temp < candies){
            if(temp + t > candies){
                ans[ind] = ans[ind] + candies - temp;
                temp = candies;
            }else{
                ans[ind] += t;
                temp += t;
            }
            t++;
            ind++;
            ind%=num_people;
        }
        return ans;
    }
};
