class Solution {
public:
    int distMoney(int money, int children) {
        if(money < children)return -1;
        money -= children;
        int ans = money/7;
        if(ans > children)return children - 1;
        if(money%7 == 0)return ans;

        if(ans == children)return ans - 1;
        if(money%7 != 3)return ans;
        if(money%7 == 3){
            if(ans == children - 1)return ans - 1;
            else return ans;
        }
        return -1;
    }
};
