class Solution {
public:
    vector<string> divideString(string s, int k, char fill) {
        int n = s.length();
        vector<string> ans;
        if(n%k == 0){
            for(int i= 0;i<n;i+=k){
                ans.push_back(s.substr(i,k));
            }
        }

        else{
            int rem = n%k;

            for(int i = 0;i<n-rem;i+=k){
                ans.push_back(s.substr(i,k));
            }

            string final = s.substr(n-rem , n);

            for(int i = 0;i<k-rem;i++){
                final+=fill;
            }







            ans.push_back(final);


        }
        return ans;

    }


};
