class Solution {
public:
    int minGroups(vector<vector<int>>& arr) {
        sort(arr.begin(), arr.end());
        multiset<int> st;
        st.insert(arr[0][1]);
        for(int i = 1; i < arr.size(); i++){
            if(*st.begin() < arr[i][0]){
                st.erase(st.begin());
                st.insert(arr[i][1]);
            }else{
                st.insert(arr[i][1]);
            }
        }
        return st.size();
    }
};
