class Solution {
public:
    long long dividePlayers(vector<int>& s) {
        sort(begin(s),end(s));
        long long ans=0;
        int i=0,j=s.size()-1;
        int cm=s[i]+s[j];
        while(i<j){
            if(s[i]+s[j]==cm){
                ans+=s[i]*s[j];
            }else return -1;
            i++;
            j--;
        }
        return ans;
    }
};
