class Solution {
public:
    bool divisorGame(int n) {
        vector<bool> dp(n+1, false);
        if(n<2){return false;}
        dp[0] = false;
        dp[1] = false;
        dp[2] = true;
        for(int i=3;i<=n;i++){
            for(int k=1;k<i;k++){
                if(i%k == 0) dp[i] = (dp[i] || !dp[i - k]);
            }
        }
        return dp[n];
    }
};
