int dp[505][505];
class Solution {
public:
    int helper(string &a, string &b, int i, int j){
        if(i == -1 && j == -1){
            return 0;
        }
        if(i == -1){
            return j + 1;
        }
        if(j == -1){
            return i + 1;
        }
        if(dp[i][j] != -1){return dp[i][j];}
        if(a[i] == b[j]){
            return dp[i][j] = helper(a, b, i - 1, j - 1);
        }else{
            return dp[i][j] = 1 + min(helper(a, b, i - 1, j - 1),
                       min(helper(a, b, i - 1, j), helper(a,b,i,j-1)));
        }

    }
    int minDistance(string word1, string word2) {
        memset(dp, -1, sizeof(dp));
        return helper(word1, word2, word1.length() - 1, word2.length() - 1);     
    }
};
