int dp[1005][3];
class Solution {
public:

    int helper(int n, int k)
    {
        if(k < 0)return 0;
        if (n == 0 || n == 1)
        {
            return n;
        }
        if (k == 1)
        {
            return n;
        }
        if( dp[n][k] != -1)return  dp[n][k];
        int res = 1e8;
        for (int i = 1; i <= n; i++)
        {
            int eggBroke = 1 + helper(i - 1, k - 1);
            int notEggBroke = 1 + helper(n - i, k);
            res = min(res, max(eggBroke, notEggBroke));
        }
        return dp[n][k] = res;
    }
    int twoEggDrop(int n) {
        memset(dp, -1, sizeof(dp));
        return helper(n, 2);
    }
};
