class Solution {
public:
    int equalPairs(vector<vector<int>>& grid) {
        int n = grid.size(), ans = 0;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < n; j++){
                vector<int> arr;
                for(int k= 0; k< n; k++){
                    arr.push_back(grid[k][j]);
                }
                if(grid[i] == arr)ans++;    
            }
        }
        return ans;
    }
};
