class Solution {
public:
    int titleToNumber(string s) {
        int power = 0;
        int ans = 0;
        for(int i = s.size() - 1;i>=0;i--){
            int num = (s[i] - 'A') + 1;
            cout<<num<<' ';
            int curr = pow(26,power);
            power++;
            ans += num * curr;            
        }
        return ans;
    }
};
