class Solution {
public:
    string convertToTitle(int columnNumber) {
        string s = "";
        int n = columnNumber;
        int d = log2(n)/log2(26) + 1;
        while(n > 0 && d--){
            int temp = n%26;
            char c;
            if(temp == 0){
                c = 'Z';
                n-=26;
            }else{
                c = 'A' + temp - 1;
                n -= temp;
            }
            n/=26;
            s.push_back(c);
        }
        reverse(s.begin(), s.end());
        return s;
    }
};
