class Solution {
public:
    vector<int> fairCandySwap(vector<int>& a, vector<int>& b) {
        long long int s1 = accumulate(a.begin(), a.end(), 0);
        long long int s2 = accumulate(b.begin(), b.end(), 0);
        sort(a.begin(), a.end());
        sort(b.begin(), b.end());

        for(auto i : a){
             int j = (s2 - s1 + 2 * i)/2;
            if(binary_search(b.begin(), b.end(), j)){
                return {i, j};
            }
        }
        return {-1, -1};
    }
};
