class Solution {
public:
    bool check(vector<int> &cookies, int k, int mid){
        int cnt = 1, s = 0;
        for(auto i : cookies){
            s += i;
            if(s > mid){
                s = i;
                cnt++;
            }
            if(s > mid)return false;
        }
        return cnt <= k;
    }
    int distributeCookies(vector<int>& cookies, int k) {
        sort(cookies.begin(), cookies.end());
        int lo = 0, hi = 1e9, ans = -1;
        vector<int> temp;
        while(lo <= hi){
            temp = cookies;
            int mid = (lo + hi)/2;
            int f = 0;
            do{
                if(check(temp, k, mid)){
                    f = 1;
                    break;
                }
            }while(next_permutation(temp.begin(), temp.end()));
            if(f){
                ans = mid;
                hi = mid - 1;
            }else{
                lo = mid + 1;
            }
        }
        return ans;
    }
};
