    vector<int> finalPrices(vector<int>& prices) {
        vector<int> v;

        for(int i=0;i<prices.size();i++){
            int price = prices[i];
            int cnt = 0;
            for(int j = i + 1;j<prices.size();j++){
                if(prices[j] <= prices[i]){
                    v.push_back(prices[i] - prices[j]);
                    cnt = 1;
                    break;
                }
            }

            if(cnt == 0) v.push_back(prices[i]);
        }

        return v;

    }
};
