class Solution {
public:
    vector<int> findPeakGrid(vector<vector<int>>& mat) {
        vector<int> p;
        int ans = -1;
        for(int i = 0; i < mat.size(); i++){
            for(int j = 0; j < mat[0].size(); j++){
                if(ans < mat[i][j])p = {i, j}, ans = mat[i][j];
            }
        }
        return p;
    }
};
