class Solution {
public:
    vector<int> findDuplicates(vector<int>& arr) {
        vector<int> ans;

    int n =arr.size();
    int i= 0;
    while(i<n){
        int corrindex = arr[i]-1;

        if(arr[i]!=arr[corrindex]){
            swap(arr[i], arr[corrindex]);
        }
        else{
            i++;
        }
    }

    for(int i =0;i<n;i++){
        if((i+1)!= arr[i]){
            ans.push_back(arr[i]);
        }
    }
    return ans;
    }
};
