class Solution {
public:
    vector<int> findLonely(vector<int>& nums) {
        unordered_map<int,int> umap;
        for(auto i:nums){
            umap[i]++;
        }
        vector<int>ans;
        for(auto iter: nums){
            if(umap[iter]==1){
                if(!umap.count(iter+1)&&!umap.count(iter-1)){
                    ans.push_back(iter);
                }
            }
        }
        return ans;
    }
};
