class Solution {
public:
    vector<int> findDisappearedNumbers(vector<int>& nums) {
        map<int, int> m;
        for(auto i : nums)m[i]++;
        vector<int> ans;
        for(int i = 1 ; i <= nums.size();i++){
            if(m[i] == 0)ans.push_back(i);
        }
        return ans;
    }
};
