class Solution {
public:
    int findCenter(vector<vector<int>>& edges) {
        vector<int> umap(edges.size() +2, 0);
        for(auto i : edges){
            for(auto j : i){
                umap[j]++;
            }
        }
        for(int i = 0; i<=edges.size() + 1;i++){
            if(umap[i] == edges.size()){
                return i;
            }
        }
        return -1;
    }
};
