class Solution {
public:
    int findClosestNumber(vector<int>& nums) {
       int ans=INT_MIN;
        int mn=INT_MAX;
       for(auto i:nums){
           if(abs(i)<=mn){
               if(ans==abs(i)&&i>ans){
                   ans=i;
               }else if(ans!=abs(i)) ans=i;
               mn=abs(i);
           }
       }
        return ans;

    }
};
