class Solution {
public:


string getString(char x)
{
    string s(1, x);

    return s;  
}
    vector<string> commonChars(vector<string>& words) {
        map<char, int> m;
        for(auto i : words[0]){
            m[i]++;
        }

        for(int i=1;i<words.size();i++){

            map<char, int> mp;
            for(auto j : words[i]){
                mp[j]++;
            }

            for(auto p : m){
                m[p.first] = min(mp[p.first], m[p.first]);
            }

        }


        vector<string> ans;
        for(auto i : m){
            if(i.second > 0){

                while(i.second--)
                ans.push_back(getString(i.first));
            }
        }

        return ans;
    }
};
