class Solution {
public:
    vector<int> searchRange(vector<int> &nums, int target)
{
    int start = -1;
    int end = -1;
    int lo = 0, hi = nums.size() - 1;
    while (lo <= hi)
    {
        int mid = (hi + lo) / 2;
        if (nums[mid] == target)
        {
            start = mid;
            hi = mid - 1;
        }
        else if (nums[mid] < target)
        {
            lo = mid + 1;
        }
        else
        {
            hi = mid - 1;
        }
    }

    //
    lo = 0, hi = nums.size() - 1;
    while (lo <= hi)
    {
        int mid = (hi + lo) / 2;
        if (nums[mid] == target)
        {
            end = mid;
            lo = mid + 1;
        }
        else if (nums[mid] < target)
        {
            lo = mid + 1;
        }
        else
        {
            hi = mid - 1;
        }
    }
    return {start, end};
}   
};
