class Solution {
public:
    bool validPath(int n, vector<vector<int>>& edges, int source, int destination) {
        vector<vector<int>> adj(n);
        for(auto i : edges){
            adj[i[0]].push_back(i[1]);
            adj[i[1]].push_back(i[0]);
        }
        vector<int> vis(n, 0);
        queue<int> q;
        q.push(source);
        vis[source] = 1;

        while(!q.empty()){
            int front = q.front();
            q.pop();
            if(front == destination){
                return true;
            }
            for(auto i : adj[front]){
                if(vis[i]) continue;
                vis[i] = true;
                q.push(i);
            }
        }
        return false;
    }
};
