class Solution {
public:
    vector<int> findClosestElements(vector<int>& arr, int k, int x) {
        int ind = lower_bound(arr.begin(), arr.end(), x) - arr.begin();
        vector<pair<int, int>> t;
        for(int i = max(0, ind - k); i <= min((int)arr.size() - 1, ind + k);i++){
            t.push_back({abs(x - arr[i]), arr[i]});
        }
        vector<int> ans;
        sort(t.begin(), t.end());
        for(int i =0 ; i < k;i++){
            ans.push_back(t[i].second);
        }
        sort(ans.begin(), ans.end());
        return ans;
    }
};
