class Solution {
public:
    int smallestDistancePair(vector<int>& nums, int k) {
        int n = nums.size();
        sort(nums.begin(), nums.end());
        int  s = 0;
        int e = nums.back() - nums.front();

        while(s < e) {
            int mid = (s + e) >> 1;
            int j = 0;
            int cnt = 0;

            for(int i = 0; i < nums.size(); i++) {
                while(j < n and nums[j] - nums[i] <= mid) j++;
                cnt += (j - i - 1);

            }
            if(cnt >= k) {
                e = mid;
            }
            else {
                s = mid + 1;
            }
        }
        return s;
    }
};
