class Solution {
    public int findNumbers(int[] nums) {
        int n = nums.length;
        int answer = 0;
        for(int i = 0;i<n;i++){
            int ans = countdigit(nums[i]);
            if(ans%2==0){
                answer+=1;
            }
        }
        return answer;
    }

    static int countdigit(int n){
        if(n<0){
            n = n*-1;
        }
        int ans =(int)(Math.log10(n))+1;
        return ans;

    }
}
