class Solution {
public:
    vector<int> findOriginalArray(vector<int>& changed) {
        if(changed.size() == 0)return {};
        map<int, int> mp;
        for(auto i : changed)mp[i]++;
        vector<int> ans;
        for(auto &i : mp){
            if(i.first%2)continue;
            int _min = 0;
            if(i.first == 0)
                _min = i.second/2;
            else
                _min = min(mp[i.first], mp[i.first/2]);
            mp[i.first] -= _min;
            mp[i.first/2] -= _min;
            for(int j = 0; j < _min; j++){
                ans.push_back(i.first/2);
            }
        }
        if(ans.size() * 2 != changed.size())return {};
        return ans;
    }
};
