class Solution
{
public:
    int findPeakElement(vector<int> &nums)
    {
        int n = nums.size();
        int start = 0, end = n - 1;
        while (start <= end)
        {
            int mid = start + (end - start) / 2;
            long long prev = mid - 1;
            long long next = mid + 1;
            if (prev == -1)
            {
                prev = LLONG_MIN;
            }
            else
            {
                prev = nums[prev];
            }

            if (next == n)
            {
                next = LLONG_MIN;
            }
            else
            {
                next = nums[next];
            }
            if (prev < nums[mid] && next < nums[mid])
            {
                return mid;
            }
            else if (prev < nums[mid] && nums[mid] < next)
            {
                start = mid + 1;
            }
            else
            {
                end = mid - 1;
            }
        }
        return start;
    }
};
