class Solution {
public:
    vector<vector<int>> findWinners(vector<vector<int>>& m) {
        vector<int> in_deg(100005, 0);

        for(auto i : m){
            in_deg[i[1]]++;
        }
        vector<vector<int> > ans;
        vector<int> res;
        for(auto i : m) {

            if(in_deg[i[0]] == 0) {
                res.push_back(i[0]);
            }
        }
        sort(res.begin(), res.end());

        auto it = unique(res.begin(), res.end());
        res.resize(distance(res.begin(), it));
        ans.push_back(res);
        res.clear();
         for(auto i : m) {

            if(in_deg[i[0]] == 1) {
                res.push_back(i[0]);
            }
              if(in_deg[i[1]] == 1) {
                res.push_back(i[1]);
            }
        }
          sort(res.begin(), res.end());
          it = unique(res.begin(), res.end());
        res.resize(distance(res.begin(), it));
        ans.push_back(res);
        return ans;


    }
};
