typedef long long ll;
class Solution {
public:
    long long findScore(vector<int>& nums) {
        map<ll, vector<ll>> m;
        ll n = nums.size();
        for(ll i = 0; i < n;i++){
            m[nums[i]].push_back(i);
        }
        vector<ll> vis(n + 1, 0);
        ll score = 0;
        for(auto i : m){
            for (auto j : i.second){
                if(vis[j] == 1)continue;
                vis[j] = 1;
                vis[j + 1] = 1;
                if(j != 0)
                    vis[j - 1] = 1;
                score += i.first;
            }
        }
        return score;
    }
};
