class Solution {
public:
    bool findSubarrays(vector<int>& nums) {
        for(int i = 0; i < nums.size() - 1; i++){
            long long sum = nums[i] + nums[i + 1];
            for(int j = i + 1; j < nums.size() - 1; j++){
                long long s = nums[j] + nums[j + 1];
                if(s == sum)return true;
            }
        }
        return false;
    }
};
