class Solution {
public:
    string subStrHash(string s, int p, int m, int k, int hash) {
        reverse(s.begin(), s.end());
        long long pow = 1;
        long long ans = 0;
        long long n = s.length();
        vector<int> h(n, -1);
        for(int i = 0; i < n; i++) {
            if(i < k) {
                pow = pow * p%m;
            }

            ans = ans * p + (s[i] - 'a' + 1);
            ans %= m;
            if(i - k >= 0) {
                ans = ans - (s[i - k] - 'a' + 1) * pow;
                ans %= m;
                ans = (ans+m)%m;
            }
            if(i >= k - 1) {
                h[i] = ans;
            }

        }
        reverse(s.begin(), s.end());
        reverse(h.begin(), h.end());
        for(int i = 0; i < h.size(); i++) {
            if(h[i] == hash) {
                return s.substr(i, k);

            }
        }
        return "";

    }
};
