class Solution {
public:
    int findTheDistanceValue(vector<int>& arr1, vector<int>& arr2, int d) {
        sort(arr1.begin(), arr1.end());
        sort(arr2.begin(), arr2.end());
        int ans = 0;
        for(auto i : arr1){
            int ind1 = lower_bound(arr2.begin(), arr2.end(), i)  - arr2.begin();
            int ind2 = upper_bound(arr2.begin(), arr2.end(), i + d) - arr2.begin();
            int ind3 = lower_bound(arr2.begin(), arr2.end(), i) - arr2.begin();
            int ind4 = lower_bound(arr2.begin(), arr2.end(), i - d) - arr2.begin();
            if((ind2 - ind1) + (ind3 - ind4) == 0)ans++;
        }
        return ans;
    }
};
