class Solution {
public:
    vector<int> distinctDifferenceArray(vector<int>& nums) {
        map<int, int> s, p;
        int n = nums.size();
        vector<int> pre(n, 0), suff(n, 0), ans(n);
        for(int i = 0; i < n; i++){
            p[nums[i]]++;
            pre[i] = p.size();
        }
        for(int i = n-  1; i >=0;i--){
            suff[i] =s.size();
            s[nums[i]]++;
        }
        for(int i = 0; i < n;i ++){
            ans[i] = -suff[i] + pre[i];
        }
        return ans;
    }
};
